mapper.select:
select * from
sys_vegetable where name = #{arg0}
and time < #{arg2} and time > #{arg1}
order by time ASC;

select * from sys_vegetable where name = #{arg0}
and time < #{arg2} and time > #{arg1}

	and area = #{arg3}

order by time ASC;

SELECT * FROM vegetablebigdata.sys_vegetable
where time >= date_sub(curdate(),interval #{arg0} day)
and time <= curdate() 

	and name = #{arg1}


	and area = #{arg2}

SELECT name,area,max(h_price) as max_h_price,min(h_price) as min_h_price, max(l_price) as max_l_price, min(l_price) as min_l_price, max(ave_price) as max_ave_price, min(ave_price) as min_ave_price FROM vegetablebigdata.sys_vegetable where time >= date_sub(curdate(),interval #{arg0} day) and time <= curdate()
and name = #{arg1}
and area = #{arg2}
